//													  DIRECTIVES													 //

/*#############################             ###############################          ################################*/
/*#############################             ###############################          ################################*/
/*#############################             ###############################          ################################*/

module_core.directive('dateDuJour', ['$timeout', function($timeout){

	return {
		scope : {
			date : "="
		},
		restrict : 'AE',

		link : function(scope, element, attributes){

			scope.$watch('date', function(newValue, oldValue){

				if (newValue !== oldValue){
					element.html(newValue);					
				}
			});
		}
	}

}]);





